import { HubConnectionBuilder, LogLevel } from '@microsoft/signalr';
import { useState } from 'react';
import './App.css';
import WaitingRoom from './components/waitingroom';
import ChatRoom from './components/ChatRoom';

function App() {
  const [conn, setConnection] = useState(null);
  const [messages, setMessages] = useState([]);

  const joinChatRoom = async (username, chatroom) => {
    try {
      const connection = new HubConnectionBuilder()
        .withUrl('http://localhost:5141/chat')
        .configureLogging(LogLevel.Information)
        .build();

      // Listening to SignalR events
      connection.on("JoinSpecificChatRoom", (username, msg) => {
        console.log("msg: ", msg);
      });

      connection.on("ReceiveSpecificMessage", (username, msg) => {
        setMessages((prevMessages) => [...prevMessages, { username, msg }]);
      });

      // Start the connection and join the chatroom
      await connection.start();
      await connection.invoke("JoinSpecificChatRoom", { username, chatroom });

      setConnection(connection);
    } catch (error) {
      console.error("Connection error: ", error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <main className="container mx-auto p-5">
        <div className="text-center mb-5">
          <h1 className="text-3xl font-bold">Welcome to the Chat App</h1>
        </div>
        {!conn ? (
          <WaitingRoom joinChatRoom={joinChatRoom} />
        ) : (
          <ChatRoom messages={messages} />
        )}
      </main>
    </div>
  );
}

export default App;
